mapper.update:
update admin_user set modify_time = #{modifyTime}

    ,nickname = #{nickname}


    ,avatar = #{avatar}


    ,email = #{email}

where id = #{id}

mapper.select:
select c.`id` adminId,c.`username`,c.`nickname`,c.`email`,c.`avatar`,c.`is_enabled` enabled,c.`login_total` loginTotal, c.`last_login_ip` lastLoginIp,c.`create_time` createTime,c.`modify_time` modifyTime,GROUP_CONCAT(c.`role_id`) roleIds from ( select a.`id` ,a.`username`,a.`nickname`,a.`email`,a.`avatar`,a.`is_enabled`,a.`login_total` ,a.`last_login_ip` , a.`last_login_time` , a.`create_time` , a.`modify_time`,b.role_id from admin_user a left join admin_user_role b on b.`admin_id` = a.`id` where 1
and a.`username` like "%${username}%"
and a.`nickname` like "%${nickname}%"
and a.`is_enabled` = 1
and a.`is_enabled` = 0
and b.`role_id` = #{roleId}
) as c GROUP BY c.`id` order by ${sortField} ${sortType}

select `id`,`username`,`nickname`, `password`, `create_time` createTime, `modify_time`
modifyTime, `avatar`,`is_enabled` `enabled`,`last_login_time` lastLoginTime,`email`
from admin_user where `id`= #{adminId}
